// Parse incoming payload (from GitHub webhook or manual trigger)
const payload = $input.first().json;

// Extract data
item.site_name = payload.site_name || 'brightgift';
item.agent_name = payload.agent_name;
item.blog_slug = payload.blog_slug;
item.topic = payload.topic || 'general';
item.current_phase = payload.current_phase;

// Validate required fields
if (!item.agent_name || !item.blog_slug || !item.current_phase) {
  throw new Error('Missing required fields: agent_name, blog_slug, current_phase');
}

// Channel mapping for BrightGift
const siteAgentChannels = {
  'brightgift': {
    'SEOAgent': 'C097J4EGEN9', // #brightgift-seo-agent
    'BlogAgent': 'C0987CMJN1E', // #brightgift-blog-agent
    'ReviewAgent': 'C097G9YCQT0', // #brightgift-review-agent
    'ImageAgent': 'C0973KH68EB', // #brightgift-image-agent
    'PublishingAgent': 'C097J4WA0LD' // #brightgift-publishing-agent
  }
};

const siteChannels = siteAgentChannels[item.site_name];
if (!siteChannels) {
  throw new Error(`Site '${item.site_name}' not configured`);
}

item.channel_id = siteChannels[item.agent_name];
if (!item.channel_id) {
  throw new Error(`Agent '${item.agent_name}' not configured for site '${item.site_name}'`);
}

// Repository mapping
const repoMapping = {
  'brightgift': 'your-org/brightgift-content-system'
};

const repo = repoMapping[item.site_name] || 'your-org/multi-agent-content-system';

// Convert agent name to folder name
const agentFolder = item.agent_name.toLowerCase().replace('agent', '');

// Construct Cursor command
item.slack_text = `@Cursor [repo=${repo}] review instructions from agents/${agentFolder}/instructions.md and then execute the following task: run ${item.agent_name} for ${item.blog_slug} topic: ${item.topic} phase: ${item.current_phase} site: ${item.site_name}`;

console.log(`Routing ${item.agent_name} for ${item.blog_slug} to channel ${item.channel_id}`);

return item;
