// Parse approval payload from dashboard
const payload = $input.first().json;

// Initialize item object
const item = {};

// Extract approval data
item.site_name = payload.site_name || 'brightgift';
item.blog_slug = payload.blog_slug;
item.topic = payload.topic || 'general';
item.current_phase = 'PUBLISHING';
item.agent_name = 'PublishingAgent';
item.approval_status = payload.approval_status || 'approved'; // approved/rejected
item.pr_number = payload.pr_number;

// Validate required fields
if (!item.blog_slug) {
  throw new Error('Missing required field: blog_slug');
}

// Channel mapping for BrightGift
const siteAgentChannels = {
  'brightgift': {
    'PublishingAgent': 'C097J4WA0LD' // #brightgift-publishing-agent
  }
};

const siteChannels = siteAgentChannels[item.site_name];
if (!siteChannels) {
  throw new Error(`Site '${item.site_name}' not configured`);
}

item.channel_id = siteChannels[item.agent_name];
if (!item.channel_id) {
  throw new Error(`Agent '${item.agent_name}' not configured for site '${item.site_name}'`);
}

// Repository mapping
const repoMapping = {
  'brightgift': 'your-org/brightgift-content-system'
};

const repo = repoMapping[item.site_name] || 'your-org/multi-agent-content-system';

// Construct efficient Cursor command with approval context (no instruction review)
item.slack_text = `@Cursor [repo=${repo}] run ${item.agent_name} for ${item.blog_slug} topic: ${item.topic} phase: ${item.current_phase} site: ${item.site_name} approval_status: ${item.approval_status} pr_number: ${item.pr_number}`;

console.log(`Publishing approval triggered for ${item.blog_slug} with status: ${item.approval_status}`);

return item;
